/*
 * Главный файл стилей.
 * Все паршалы подключаются сюда.
 */

// Стили для файла с примером HTML-фрагмента
@import './utils/main';
@import './base/main';
@import './components/main';
@import './layout/main';

sources.sass\\utils\\_visually-hidden.scss:
.visually-hidden {
  position: absolute;
  width: 1px;
  height: 1px;
  margin: -1px;
  border: 0;
  padding: 0;

  white-space: nowrap;
  clip-path: inset(100%);
  clip: rect(0 0 0 0);
  overflow: hidden;
}

sources.sass\\base\\_base.scss:
*,
*::after,
*::before {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

h1,
h2,
h3,
h4,
h5,
h6,
ul,
p {
  margin: 0;
  padding: 0;
}

button {
  padding: 0;
}

svg,
img {
  display: block;
  max-width: 100%;
  height: auto;
}

input {
  cursor: pointer;
}

a {
  display: inline-block;
  text-decoration: none;
}

ul {
  list-style: none;
}

