sources.sass\\components\\_footer.scss:
footer {
  background-color: $light-bg-color;
}
.footer-container {
  padding: 113px 20px 43px 20px;

  @media screen and (min-width: 768px) {
    padding-top: 165px;
    padding-bottom: 91px;
    padding-left: 36px;
    padding-right: 36px;
    display: flex;
    justify-content: space-between;
  }
  @media screen and (min-width: 1280px) {
    padding-bottom: 97px;
    padding-left: 115px;
    padding-right: 115px;
  }
}
.footer-title {
  font-family: $fontTitan;
  text-transform: uppercase;
  width: 234px;
  @media screen and (min-width: 768px) {
    width: 340px;
  }
  @media screen and (min-width: 1280px) {
    width: 474px;
  }

  &__text {
    @include fontStyle(400, 18, 21, 1);
    color: $pre-title-color;
    margin-bottom: 17px;

    @media screen and (min-width: 768px) {
      @include fontStyle(400, 26, 30, 4);
    }

    @media screen and (min-width: 1280px) {
      @include fontStyle(400, 30, 34, 4);
    }
    &--second {
      @include fontStyle(400, 34, 39, 4);
      color: $accent-color;

      @media screen and (min-width: 768px) {
        @include fontStyle(400, 48, 55, 6);
      }

      @media screen and (min-width: 1280px) {
        @include fontStyle(400, 58, 66, 4);
      }
    }
    &--second {
      margin-bottom: 43px;
    }
  }
}

.footer-social__contacts {
  font-family: $fontDM;
  @include fontStyle(700, 12, 22, 0);

  @media screen and (min-width: 768px) {
    @include fontStyle(700, 14, 26, 0);
  }

  @media screen and (min-width: 1280px) {
    @include fontStyle(700, 16, 30, 2);
  }

  &--link {
    transition: 250ms cubic-bezier(0.4, 0, 0.2, 1);
    color: $accent-color;

    &:hover,
    &:focus {
      color: $socials-color;
    }
  }
}

.footer-signature__text {
  @include fontStyle(700, 14, 21, 0);
  color: $light-text-color;

  @media screen and (min-width: 1280px) {
    @include fontStyle(700, 14, 21, 2);
  }
}

.footer-signature {
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  padding-top: 22px;
  padding-bottom: 25px;
  border-top: 1px solid $pseudo-color;

  &__text {
    width: 150px;
    @media screen and (min-width: 768px) {
      width: 276px;
    }
  }
}

// Шестерня
.config {
  position: absolute;
  z-index: 3;

  top: 33%;
  right: 6%;

  fill: rgba(0, 0, 0, 0.5);
  border: none;
  background-color: $config-bgc-color;

  @media screen and (min-width: 768px) {
    top: 10%;
    right: 10%;
  }

  &:hover {
    animation-play-state: paused;
  }

  animation-name: spin;
  animation-duration: 5s;
  animation-timing-function: linear;
  animation-iteration-count: infinite;

  @keyframes spin {
    0% {
      transform: rotate(0deg);
    }

    100% {
      transform: rotate(360deg);
    }
  }

  @media screen and (min-width: 768px) {
    top: 26%;
    right: 3%;
  }
}

