  width: 280px;
  height: 598px;

  @media screen and (max-width: 767.98px) {
    &:not(:last-child) {
      margin-bottom: 17px;
    }
  }

  @media screen and (min-width: 768px) {
    width: 220px;
    height: 596px;
    margin-bottom: 0;
  }

  @media screen and (min-width: 1280px) {
    width: 329px;
    height: 735px;
    margin-bottom: 0;
  }
}

.products__card {
  &--iceCream {
    background-color: $hero-bg-color;
    border-radius: 24px;
    transition: 250ms cubic-bezier(0.4, 0, 0.2, 1);
    &:hover,
    &:focus {
      box-shadow: 0px 0px 25px 0px $hero-bg-color;
      transform: scale(1.05);
    }
  }

  &--iceCoffee {
    background-color: $ice-coffee-color;
    border-radius: 24px;
    transition: 250ms cubic-bezier(0.4, 0, 0.2, 1);
    &:hover,
    &:focus {
      box-shadow: 0px 0px 25px 0px $ice-coffee-color;
      transform: scale(1.05);
    }
  }

  &--milkShakes {
    background-color: $milkshake-color;
    border-radius: 24px;
    transition: 250ms cubic-bezier(0.4, 0, 0.2, 1);
    &:hover,
    &:focus {
      box-shadow: 0px 0px 25px 0px $milkshake-color;
      transform: scale(1.05);
    }
  }
}

