sources.sass\\layout\\_contacts.scss:
.contacts {
  padding-top: 86px;
  background: linear-gradient(to bottom, $main-color 170px, $light-bg-color 170px);

  @media screen and (min-width: 768px) {
    padding-top: 119px;
    background: linear-gradient(to bottom, $main-color 197px, $light-bg-color 197px);
  }

  @media screen and (min-width: 1280px) {
    padding-top: 132px;
    background: linear-gradient(to bottom, $main-color 234px, $light-bg-color 234px);
  }
}

.contacts__container {
  padding-left: 20px;
  padding-right: 20px;

  @media screen and(min-width:768px) {
    padding-left: 36px;
    padding-right: 36px;
  }
  
  @media screen and (min-width: 1280px) {
    padding-left: 115px;
    padding-right: 115px;
    background-image: url(/images/contacts-bg-spots-1200@1x.png);
    background-repeat: no-repeat;
    background-position-y: 234px;
    background-size: contain;
    @media (min-device-pixel-ratio: 2), (min-resolution: 192dpi), (min-resolution: 2dppx) {
      background-image: url(/images/contacts-bg-spots-1200@2x.png);
    }
  }
}

.card {
  @media screen and (min-width: 768px) {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
  }
}

.card__item {
  margin-left: auto;
  margin-right: auto;
  padding: 43px 21px 43px 20px;
  width: 220px;
  height: auto;
  box-shadow: 0px 8px 30px rgba(212, 20, 67, 0.1);
  border-radius: 24px;
  font-weight: 500;
  transition: 250ms cubic-bezier(0.4, 0, 0.2, 1);
  background-color: $main-color;

  &:hover,
  &:focus {
    box-shadow: 0px 0px 10px 0px $hero-bg-color;
    transform: scale(1.05);
  }

  &:not(:last-child) {
    @media screen and (max-width: 767px) {
      margin-bottom: 20px;
    }
  }

  @media screen and (min-width: 768px) {
    flex-basis: calc((100% - 3 * 20px) / 3);
    margin-left: 10px;
    margin-right: 10px;
    padding: 50px 32px 50px 31px;
    font-size: 14px;
    line-height: 1.875em;
  }

  @media screen and (min-width: 1280px) {
    flex-basis: calc((100% - 3 * 30px) / 3);
    margin-left: 15px;
    margin-right: 15px;
    padding: 52px 44px 61px 43px;
    font-size: 16px;
  }
}

.card__thumb {
  display: flex;
  align-items: center;
  margin-bottom: 25px;

  @media screen and (min-width: 768px) {
    margin-bottom: 30px;
  }

  @media screen and (min-width: 1280px) {
    margin-bottom: 32px;
  }
}

.card__representations {
  border-radius: 4px;
  text-align: center;
  padding: 0 24px;
  line-height: 1.833em;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: $main-color;
  background-color: $ice-coffee-color;

  @media screen and (min-width: 768px) {
    padding: 0 16px;
    font-size: 16px;
  }

  @media screen and (min-width: 1280px) {
    line-height: 1.833em;
  }
}

.card__representations--color {
  padding: 0 27px;
  background-color: $milkshake-color;

  @media screen and (min-width: 768px) {
    padding: 0 16px;
  }
}

.card__city {
  line-height: 1.833em;
  letter-spacing: 0.04em;
  margin-bottom: 10px;
  color: $main-text-color;

  @media screen and (min-width: 768px) {
    margin-bottom: 14px;
  }

  @media screen and (min-width: 1280px) {
    margin-bottom: 10px;
  }
}

.card__text {
  line-height: 1.5em;
  color: $light-text-color;

  @media screen and (min-width: 768px) {
    line-height: 1.642em;
  }

  @media screen and (min-width: 1280px) {
    line-height: 1.812em;
  }
}

.card-period::before,
.card-address::before {
  margin: 25px 0;
  content: '';
  display: block;
  border: 1px solid $pseudo-color;

  @media screen and (min-width: 768px) {
    margin: 30px 0;
  }

  @media screen and (min-width: 1280px) {
    margin: 32px 0;
  }
}

.card-period__item {
  line-height: 1.833em;
  &:not(:last-child) {
    padding-bottom: 10px;

    @media screen and (min-width: 768px) {
      padding-bottom: 16px;
      line-height: 1.857em;
    }

    @media screen and (min-width: 1280px) {
      padding-bottom: 15px;
      line-height: 1.875em;
    }
  }
}

.card-period__time {
  color: $pre-title-color;
}

.card-address__link {
  line-height: 1.5em;
  color: $light-text-color;

  @media screen and (min-width: 768px) {
    line-height: 1.857em;
  }

  @media screen and (min-width: 1280px) {
    line-height: 1.875em;
  }
}

// Contacts button
.card-button {
  margin-top: 54px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;

  @media screen and (min-width: 768px) {
    margin-top: 81px;
    flex-direction: row;
  }

  @media screen and (min-width: 1280px) {
    margin-top: 86px;
  }
}

.card-button__item:not(:last-child) {
  @media screen and (max-width: 767px) {
    margin-bottom: 10px;
  }

  @media screen and (min-width: 768px) {
    margin-right: 14px;
  }

  @media screen and (min-width: 1280px) {
    margin-right: 15px;
  }
}

.card-button__button {
  display: flex;
  justify-content: center;
  align-items: center;
  text-align: center;
  padding: 12px 15px;
  font-weight: bold;
  font-size: 16px;
  line-height: 1.312;
  border-radius: 22px;
  border: transparent;
  cursor: pointer;
  color: $main-color;
  background-color: $slider-accent-color;

  @media screen and (min-width: 768px) {
    padding: 12px 27px;
  }

  @media screen and (min-width: 1280px) {
    padding: 12px 35px;
  }
}

.card-button__icon {
  margin-left: 8px;
  width: 20px;
  height: 10px;
  fill: currentColor;

  @media screen and (min-width: 768px) {
    margin-left: 13px;
  }
}

.card-button__button--color {
  padding: 12px 32px;
  background-color: $main-color;
  color: $slider-accent-color;
  transition: 250ms cubic-bezier(0.4, 0, 0.2, 1);
  &:hover,
  &:focus {
    background-color: $button-hover-color;
  }

  @media screen and (min-width: 768px) {
    padding: 12px 37px;
  }

  @media screen and (min-width: 1280px) {
    padding: 12px 39px;
  }
}

sources.sass\\layout\\_modal.scss:
.backdrop {
  position: fixed;
  width: 100%;
  height: 100%;
  padding-top: 10px;
  padding-bottom: 10px;

  background-color: rgba(0, 0, 0, 0.2);

  top: 0;
  left: 0;

  z-index: 999;

  opacity: 1;
  transition: opacity 250ms cubic-bezier(0.4, 0, 0.2, 1);

  &--is-hidden {
    opacity: 0;
    pointer-events: none;
  }
}

.modal {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 280px;
  height: 495px;
  background-color: $modal-bg-color;
  border-radius: 4px;
  transition: transform 400ms cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: 1px 1px 10px 1px $review-dots-color;

  @media screen and (min-width: 1280px) {
    width: 500px;
    height: 600px;
  }
}

.backdrop--is-hidden .modal {
  transform: translateX(-150%);
}
.form {
  display: flex;
  flex-direction: column;
  padding: 20px 30px;
  @media screen and (min-width: 1280px) {
    padding: 20px 50px;
  }
}

.form__label {
  position: relative;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  flex-direction: column;
}

.form__label-name {
  position: absolute;
  top: -5px;
  left: -5px;

  transform: translateY(-15px);
  font-size: 14px;
  line-height: 1.2;
  display: flex;
  align-items: center;
  text-align: center;
  
  color: $accent-color;

  @media screen and (min-width: 1280px) {
    font-size: 18px;
    top: -10px;
    left: 5px;
  }
}

.form__tittle--bold {
  font-family: Titan One;
  font-style: normal;
  font-weight: 400;
  font-size: 26px;
  line-height: 1.12;
  text-align: center;
  letter-spacing: 0.04em;
  text-transform: uppercase;

  color: $accent-color;

  margin-bottom: 30px;
  @media screen and (min-width: 1280px) {
    line-height: 1.14;
    font-size: 34px;
  }
}

.form__input {
  width: 240px;
  height: 30px;
  border: 1px solid (rgba(33, 33, 33, 0.2));
  border-radius: 4px;
  background-color: $modal-input-color;
  padding-left: 10px;
  padding-right: 10px;
  margin-bottom: 28px;
  transition: transform 250ms cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: 1px 1px 5px 1px $review-dots-color;

  @media screen and (min-width: 1280px) {
    width: 400px;
    height: 40px;
    margin-bottom: 35px;
  }
}

.form__label:focus-within > .form__input,
.form__label:focus-within > .form__textarea {
  box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.12), 0px 4px 4px rgba(0, 0, 0, 0.06),
    1px 4px 6px rgba(0, 0, 0, 0.16);
  stroke: $main-color;
  outline: none;
  border-color: $main-color;
}

.form__textarea {
  width: 240px;
  resize: none;
  height: 100px;
  cursor: pointer;
  background-color: $modal-input-color;
  padding: 8px;
  border: 1px solid rgba(33, 33, 33, 0.2);
  border-radius: 4px;
  margin-bottom: 20px;
  transition: transform 250ms cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: 1px 1px 5px 1px $review-dots-color;

  @media screen and (min-width: 1280px) {
    width: 400px;
    height: 130px;
    margin-bottom: 20px;

  }

  &::placeholder {
    font-size: 14px;
    color: rgba(0, 0, 0, 0.2);
    line-height: 1.25;
    @media screen and (min-width: 1280px) {
      font-size: 16px;
      line-height: 1.14;
      letter-spacing: 0.01em;
    }

    
  }
}

.form__submit-button {
  width: 100px;
  padding: 10px 32px;
  border: transparent;
  background-color: $accent-color;
  border-radius: 20px;
  color: $main-color;

  margin-left: auto;
  margin-right: auto;

  font-family: DM Sans;
  font-style: normal;
  font-weight: bold;
  font-size: 16px;
  line-height: 1.1;
  cursor: pointer;

  &:hover,
  &:focus {
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.12), 0px 4px 4px rgba(0, 0, 0, 0.06),
      1px 4px 6px rgba(0, 0, 0, 0.16);
  }
  @media screen and (min-width: 1280px) {
    width: 180px;
    font-size: 20px;
  }
}

.form__close-button {
  position: absolute;
  right: 7px;
  top: 7px;

  display: flex;
  align-items: center;
  justify-content: center;

  width: 20px;
  height: 20px;

  stroke: $main-text-color;
  background-color: $modal-input-color;
  border: none;
  border-radius: 50px;

  cursor: pointer;

  &:hover,
  &:focus {
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.12), 0px 4px 4px rgba(0, 0, 0, 0.06),
      1px 4px 6px rgba(0, 0, 0, 0.16);
  }

  @media screen and (min-width: 1280px) {
    right: 15px;
    top: 15px;
    width: 30px;
    height: 30px;
  }
}

.form__close-icon {
  @media screen and (min-width: 1280px) {
    width: 15px;
    height: 15px;
  }
}

.form__label-select {
  cursor: pointer;
  display: inline-block;
  font-family: DM Sans;
  font-size: 13px;
  color: #000;
  line-height: 1.3;
  padding-left: 8px;
  width: 150px;
  height: 30px;
  border: 1px solid (rgba(33, 33, 33, 0.2));
  border-radius: 4px;
  background-color: $modal-input-color;
  box-shadow: 0 1px 0 1px rgba(0, 0, 0, 0.04);
  margin-bottom: 35px;
  box-shadow: 1px 1px 5px 1px $review-dots-color;

  @media screen and (min-width: 1280px) {
    width: 200px;
    font-size: 16px;
    height: 40px;
  }
}

sources.sass\\layout\\_developers.scss:
.dev {
  position: absolute;
  width: 300px;
  max-height: 100%;

  background-color: $team-bgc-color;

  border-radius: 6px;

  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);

  transition: transform 250ms cubic-bezier(0.4, 0, 0.2, 1);
  @media screen and (min-width: 768px) {
    width: 350px;
  }

  @media screen and (min-width: 1200px) {
    width: 428px;
  }
  padding: 15px;
}

.backdrop--is-hidden .dev {
  transform: translate(-50%, -50%) scale(1.1);
}

.dev__close-button {
  position: absolute;
  right: -15px;
  top: -15px;

  display: flex;
  align-items: center;
  justify-content: center;

  width: 30px;
  height: 30px;

  stroke: $main-text-color;
  background-color: $modal-input-color;
  border: none;
  border-radius: 50px;

  cursor: pointer;

  &:hover,
  &:focus {
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.12), 0px 4px 4px rgba(0, 0, 0, 0.06),
      1px 4px 6px rgba(0, 0, 0, 0.16);
  }
}

.dev__title {
  font-family: $fontTitan;
  @include fontStyle(400, 22, 38.93, 6);
  color: $accent-color;
  text-align: center;
  text-transform: uppercase;

  margin-bottom: 26px;
  @media screen and(min-width:768px) {
    @include fontStyle(400, 24, 54.96, 4);
  }
  @media screen and(min-width:1280px) {
    @include fontStyle(400, 26, 66.41, 6);
  }
}

.dev__teamList {
  text-align: center;
  padding-bottom: 30px;
}

.dev__team:not(:last-child) {
  margin-bottom: 15px;
}

.dev__work {
  font-family: $fontDM;
  @include fontStyle(500, 14, 19.44, 2);
  color: $main-text-color;
  @media screen and(min-width:768px) {
    @include fontStyle(500, 16, 24.64, 2);
  }
  @media screen and(min-width:1280px) {
    @include fontStyle(500, 18, 28.16, 4);
  }
}

.dev__name {
  font-family: $fontDM;
  @include fontStyle(700, 15, 19.44, 4);
  color: $main-text-color;
  @media screen and(min-width:768px) {
    @include fontStyle(700, 17, 24.64, 4);
  }
  @media screen and(min-width:1280px) {
    @include fontStyle(700, 19, 28.16, 4);
  }
}
